INSERT INTO `naturalability_number` VALUES (1,'Chỉ số năng lực tự nhiên số 1: Tính độc lập và sự tự chủ.','Người có chỉ số này thường tỏ ra độc lập, tự tin và luôn muốn tiên phong trong các ý tưởng mới.\nTính cách này thúc đẩy họ đạt được mục tiêu cá nhân một cách độc lập và quyết đoán.\nSự tự chủ giúp họ tạo ra sự ổn định và tự tin trong quản lý cuộc sống hàng ngày.','NaturalAbility_Number'),(2,'Chỉ số năng lực tự nhiên số 2: Khả năng lắng nghe và thấu hiểu.','Có khả năng kết nối, thấu hiểu và chia sẻ một cách tốt, giúp tạo ra mối quan hệ mạnh mẽ và đáng tin cậy.\nKhả năng lắng nghe tốt giúp họ hiểu rõ nhu cầu của người khác và xây dựng mối quan hệ trọn vẹn hơn.\nSự thấu hiểu tạo ra một môi trường hòa thuận và hỗ trợ, đem lại sự ổn định và hạnh phúc trong cuộc sống.','NaturalAbility_Number'),(3,'Chỉ số năng lực tự nhiên số 3: Năng động và sáng tạo.','Sở hữu năng lượng tích cực, sẵn sàng thử nghiệm và tạo ra những ý tưởng mới, đem lại sự tươi mới và sáng tạo.\nTính linh hoạt và sáng tạo giúp họ thích ứng và thành công trong mọi tình huống, đồng thời đem lại cảm giác hứng khởi cho người xung quanh.\nNăng động và sáng tạo giúp họ vượt qua thách thức và đạt được thành công trong nhiều lĩnh vực cuộc sống.','NaturalAbility_Number'),(4,'Chỉ số năng lực tự nhiên số 4: Kiên định và tỉ mỉ.','Có khả năng kiên nhẫn, cẩn trọng và tỉ mỉ trong mọi công việc, tạo ra sự ổn định và thành công dài lâu.\nSự kiên nhẫn và tỉ mỉ giúp họ vượt qua những thử thách khó khăn và đạt được mục tiêu dài hạn.\nTính chính xác và cẩn trọng giúp họ đạt được kết quả tốt nhất trong mọi lĩnh vực của cuộc sống.','NaturalAbility_Number'),(5,'Chỉ số năng lực tự nhiên số 5: Năng lượng trẻ trung và sự linh hoạt.','Mang trong mình năng lượng tích cực, linh hoạt và sẵn lòng thích nghi với những thay đổi, tạo ra sự hứng khởi và trẻ trung.\nSự linh hoạt và sáng tạo giúp họ khám phá và thử nghiệm nhiều lựa chọn, tạo ra cơ hội mới và trải nghiệm đa dạng.\nTính cởi mở và năng động giúp họ thu hút sự chú ý và kết nối với người khác một cách dễ dàng.','NaturalAbility_Number'),(6,'Chỉ số năng lực tự nhiên số 6: Tình yêu thương và lòng nhân ái.','Người mang chỉ số này thường có trái tim ấm áp và bao dung, luôn sẵn sàng chia sẻ yêu thương và giúp đỡ người khác.\nTính yêu thương và lòng nhân ái giúp họ xây dựng môi trường hòa bình và hạnh phúc trong gia đình và cộng đồng.\nSự quan tâm và chia sẻ giúp họ thấu hiểu và tạo ra mối quan hệ gắn kết và bền vững với mọi người xung quanh.','NaturalAbility_Number'),(7,'Chỉ số năng lực tự nhiên số 7: Khả năng tự học và sáng tạo.','Có khả năng tự học và khám phá, tạo ra cơ hội cho sự phát triển cá nhân và sự nghiệp.\nSự sáng tạo và suy luận giúp họ tìm ra giải pháp mới và đem lại những đóng góp tích cực cho xã hội.\nTính tò mò và sự ham muốn khám phá giúp họ không ngừng học hỏi và phát triển trong mọi lĩnh vực cuộc sống.','NaturalAbility_Number'),(8,'Chỉ số năng lực tự nhiên số 8: Năng lực quản lý và tham vọng.','Sở hữu khả năng quản lý và lãnh đạo mạnh mẽ, tạo ra sự tập trung và tiến bộ trong công việc và sự nghiệp.\nSự quyết đoán và tham vọng giúp họ đặt ra mục tiêu cao và không ngừng nỗ lực để đạt được thành công.\nTính trung thực và công bằng giúp họ xây dựng mối quan hệ chắc chắn và đáng tin cậy với đồng nghiệp và đối tác.','NaturalAbility_Number'),(9,'Chỉ số năng lực tự nhiên số 9: May mắn và sự đáng tin cậy.','Người mang chỉ số này thường may mắn trong mọi việc, đem lại niềm vui và thành công cho bản thân và người khác.\nTính đáng tin cậy và trách nhiệm giúp họ làm việc hiệu quả và xây dựng mối quan hệ ổn định và bền vững.\nSự lạc quan và tích cực giúp họ vượt qua mọi thách thức và tận hưởng cuộc sống một cách trọn vẹn.','NaturalAbility_Number'),(11,'Chỉ số năng lực tự nhiên số 11: Suy luận tâm linh và kết nối với trực giác.','Có khả năng suy luận và kết nối với thế giới tâm linh, giúp họ hiểu rõ hơn về bản thân và mục đích cuộc sống.\nTính nhạy bén và sự linh hoạt giúp họ đưa ra quyết định thông minh và đạt được sự thành công trong mọi lĩnh vực.\nSự kết nối với trực giác giúp họ cảm nhận và hướng dẫn đúng đắn trong cuộc sống và sự nghiệp.','NaturalAbility_Number'),(22,'Chỉ số năng lực tự nhiên số 22: Trí tưởng tượng và khả năng giao tiếp.','Sở hữu trí tưởng tượng phong phú và khả năng giao tiếp tốt, tạo ra sự ảnh hưởng và tầm nhìn lớn cho mục tiêu của họ.\nSự sáng tạo và linh hoạt giúp họ tạo ra những giải pháp mới và đột phá trong công việc và sự nghiệp.\nTính linh hoạt và thấu hiểu giúp họ kết nối và làm việc hiệu','NaturalAbility_Number');
